import React from 'react';
import { StyleSheet, Text, View, ImageBackground, FlatList, TouchableOpacity } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';

import TextCenter from '../components/TextCenter'

const DATA = [
    {
      "nome": "Forno Airfryer",
      "descricao": "Ovenfryer 10L Cuisinart 127V",
      "preco": 1481.89,
      "imagem": "AirFryer1.png"
    },
    {
      "nome": "Fritadeira Elétrica",
      "descricao": "Airfryer Viva Black Edition PHILIPS WALITA 220V",
      "preco": 1253.89,
      "imagem": "AirFryer2.png"
    },
    {
      "nome": "Fritadeira Elétrica",
      "descricao": "Airfryer Viva Philips Walita N 220V",
      "preco": 1128.49,
      "imagem": "AirFryer3.png"
    },
    {
      "nome": "Fritadeira Elétrica",
      "descricao": "Turbofryer Avance Philips Walita 127V",
      "preco": 2165.89,
      "imagem": "AirFryer4.png"
    },
    {
      "nome": "Geladeira Brastemp",
      "descricao": "Frost Free Duplex 375 litros cor Inox com Espaço Adapt 110V",
      "preco": 2249.10,
      "imagem": "Geladeira1.png"
    },
    {
      "nome": "Geladeira Electrolux",
      "descricao": "Cycle Defrost 362 Litros DC44 Branco - 127V",
      "preco": 1850.50,
      "imagem": "Geladeira2.png"
    },
    {
      "nome": "Refrigerador Brastemp",
      "descricao": "BRM44HK Frost Free com Gavetão de Legumes Fresh Zone Inox - 375L - 220V",
      "preco": 2338.99,
      "imagem": "Geladeira3.png"
    },
    {
      "nome": "Refrigerador Consul",
      "descricao": "CRD37EB com Prateleiras Removíveis e Reguláveis Branco - 334L - 110v",
      "preco": 1597.06,
      "imagem": "Geladeira4.png"
    },
    {
      "nome": "Torradeira DeLonghi",
      "descricao": "TTM400 Inox 600W 6 Temperaturas (110)",
      "preco": 128.90,
      "imagem": "Torradeira1.png"
    },
    {
      "nome": "Torradeira Easyline",
      "descricao": "Preta - 220v - Electrolux",
      "preco": 129.00,
      "imagem": "Torradeira2.png"
    },
    {
      "nome": "Torradeira Oster",
      "descricao": "Square Retro Toaster - 220V",
      "preco": 169.90,
      "imagem": "Torradeira3.png"
    },
    {
      "nome": "Torradeira Cuisinart",
      "descricao": "para 2 Fatias com Visor em Vidro 127V",
      "preco": 569.90,
      "imagem": "Torradeira4.png"
    }
  ]
  
  const Item = ({ title }) => (
    <View style={styles.item}>
      <Text style={styles.title}>{title}</Text>
    </View>
  );
  
  const renderItem = ({ item }) => (
    <Item title={item.title} />
  );

  export default function Produtos() {
    return (
        <LinearGradient
          colors={['#fa3e9b', '#fa8e6d', '#fbbf1f']}
          style={styles.gradient}
          start={{ x: 0, y: 0 }}
          end={{ x: 1, y: 1 }}
          >
  
      <View style={styles.inputContainer}>
        
        <View style={styles.containerUsers}>
          <FlatList
          data={DATA}
          keyExtractor={(item) => `${item.id}`}
          renderItem={renderItem}
          />
        </View>
          
      </View>
      </LinearGradient>
    );
  }
  
  const styles = StyleSheet.create({
    gradient: {
      flex: 1,
      alignItems: 'center',
      justifyContent: 'center',
    },
    avatar: {
      width: 100,
      height: 100,
      borderRadius: 50,
      overflow: 'hidden'
    },
    inputContainer: {
      paddingTop: 5,
    },
    containerUsers: {
      flex: 7,
    },
    item: {
      backgroundColor: 'rgba(251, 233, 237, 0.2)',
      padding: 10,
      margin: 8,
      width: 350,
      borderRadius: 15,
    },
    title: {
      fontSize: 22,
      color: '#fff'
    },
    register: {
      width: 350,
      backgroundColor: '#fff',
      padding: 15,
      margin: 5,
      borderRadius: 8,
      alignItems: 'center'
    },
    botao: {
      fontWeight: 'bold',
      fontSize: 25,
      color: '#fbbf1f'
    }
  });
