import React from 'react';
import { StyleSheet, Text, View, ImageBackground, FlatList, TouchableOpacity } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';

import TextCenter from '../components/TextCenter'

const DATA = [
    {
      id: Math.random(),
      title: 'Nome'
    },
    {
      id: Math.random(),
      title: 'Sobrenome' 
    },
    {
      id: Math.random(),
      title: 'Usuario'
    },
    {
      id: Math.random(),
      title: 'E-mail'
    },
    {
      id: Math.random(),
      title: 'Senha'
    },
  ]
  
  const Item = ({ title }) => (
    <View style={styles.item}>
      <Text style={styles.title}>{title}</Text>
    </View>
  );
  
  const renderItem = ({ item }) => (
    <Item title={item.title} />
  );

  export default function Produtos() {
    return (
        <LinearGradient
          colors={['#fa3e9b', '#fa8e6d', '#fbbf1f']}
          style={styles.gradient}
          start={{ x: 0, y: 0 }}
          end={{ x: 1, y: 1 }}
          >

  
      <View style={styles.inputContainer}>
        
        <View style={styles.containerUsers}>
          <FlatList
          data={DATA}
          keyExtractor={(item) => `${item.id}`}
          renderItem={renderItem}
          />
        </View>
  
        <TouchableOpacity style={styles.register}>
           <Text style={styles.botao}>Registrar</Text>
        </TouchableOpacity>      
  
      </View>
      </LinearGradient>
    );
  }
  
  const styles = StyleSheet.create({
    gradient: {
      flex: 1,
      alignItems: 'center',
      justifyContent: 'center',
    },
    avatar: {
      width: 100,
      height: 100,
      borderRadius: 50,
      overflow: 'hidden'
    },
    inputContainer: {
      paddingTop: 5,
    },
    containerUsers: {
      flex: 7,
    },
    item: {
      backgroundColor: 'rgba(251, 233, 237, 0.2)',
      padding: 10,
      margin: 8,
      width: 350,
      borderRadius: 15,
    },
    title: {
      fontSize: 22,
      color: '#fff'
    },
    register: {
      width: 350,
      backgroundColor: '#fff',
      padding: 15,
      margin: 5,
      borderRadius: 8,
      alignItems: 'center'
    },
    botao: {
      fontWeight: 'bold',
      fontSize: 25,
      color: '#fbbf1f'
    }
  });
